</span><span><span class="hljs-keyword">CREATE</span></span><span> </span><span><span class="hljs-keyword">TABLE</span></span><span> FileSystem (
    NodeID </span><span><span class="hljs-type">INT</span></span><span> </span><span><span class="hljs-keyword">PRIMARY</span></span><span> KEY,
    NodeName </span><span><span class="hljs-type">VARCHAR</span></span><span>(</span><span><span class="hljs-number">100</span></span><span>),
    ParentID </span><span><span class="hljs-type">INT</span></span><span>,
    SizeBytes </span><span><span class="hljs-type">INT</span></span><span>
);

</span><span><span class="hljs-comment">-- Step 2: Insert sample data</span></span><span>
</span><span><span class="hljs-keyword">INSERT</span></span><span> </span><span><span class="hljs-keyword">INTO</span></span><span> FileSystem (NodeID, NodeName, ParentID, SizeBytes) </span><span><span class="hljs-keyword">VALUES</span></span><span>
(</span><span><span class="hljs-number">1</span></span><span>, </span><span><span class="hljs-string">'Documents'</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>),
(</span><span><span class="hljs-number">2</span></span><span>, </span><span><span class="hljs-string">'Pictures'</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>),
(</span><span><span class="hljs-number">3</span></span><span>, </span><span><span class="hljs-string">'File1.txt'</span></span><span>, </span><span><span class="hljs-number">1</span></span><span>, </span><span><span class="hljs-number">500</span></span><span>),
(</span><span><span class="hljs-number">4</span></span><span>, </span><span><span class="hljs-string">'Folder1'</span></span><span>, </span><span><span class="hljs-number">1</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>),
(</span><span><span class="hljs-number">5</span></span><span>, </span><span><span class="hljs-string">'Image.jpg'</span></span><span>, </span><span><span class="hljs-number">2</span></span><span>, </span><span><span class="hljs-number">1200</span></span><span>),
(</span><span><span class="hljs-number">6</span></span><span>, </span><span><span class="hljs-string">'Subfolder1'</span></span><span>, </span><span><span class="hljs-number">4</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>),
(</span><span><span class="hljs-number">7</span></span><span>, </span><span><span class="hljs-string">'File2.txt'</span></span><span>, </span><span><span class="hljs-number">4</span></span><span>, </span><span><span class="hljs-number">750</span></span><span>),
(</span><span><span class="hljs-number">8</span></span><span>, </span><span><span class="hljs-string">'File3.txt'</span></span><span>, </span><span><span class="hljs-number">6</span></span><span>, </span><span><span class="hljs-number">300</span></span><span>),
(</span><span><span class="hljs-number">9</span></span><span>, </span><span><span class="hljs-string">'Folder2'</span></span><span>, </span><span><span class="hljs-number">2</span></span><span>, </span><span><span class="hljs-keyword">NULL</span></span><span>),
(</span><span><span class="hljs-number">10</span></span><span>, </span><span><span class="hljs-string">'File4.txt'</span></span><span>, </span><span><span class="hljs-number">9</span></span><span>, </span><span><span class="hljs-number">250</span></span><span>);

</span><span><span class="hljs-comment">-- Step 3: Recursive CTE to gather hierarchy</span></span><span>
</span><span><span class="hljs-keyword">WITH</span></span><span> </span><span><span class="hljs-keyword">RECURSIVE</span></span><span> FileTree </span><span><span class="hljs-keyword">AS</span></span><span> (
    </span><span><span class="hljs-comment">-- Base case: select all nodes (start with themselves)</span></span><span>
    </span><span><span class="hljs-keyword">SELECT</span></span><span> 
        NodeID </span><span><span class="hljs-keyword">AS</span></span><span> RootID,
        NodeID </span><span><span class="hljs-keyword">AS</span></span><span> CurrentID,
        SizeBytes
    </span><span><span class="hljs-keyword">FROM</span></span><span> FileSystem

    </span><span><span class="hljs-keyword">UNION</span></span><span> </span><span><span class="hljs-keyword">ALL</span></span><span>

    </span><span><span class="hljs-comment">-- Recursive case: traverse children</span></span><span>
    </span><span><span class="hljs-keyword">SELECT</span></span><span>
        ft.RootID,
        fs.NodeID,
        fs.SizeBytes
    </span><span><span class="hljs-keyword">FROM</span></span><span> FileTree ft
    </span><span><span class="hljs-keyword">JOIN</span></span><span> FileSystem fs </span><span><span class="hljs-keyword">ON</span></span><span> fs.ParentID </span><span><span class="hljs-operator">=</span></span><span> ft.CurrentID
)
</span><span><span class="hljs-comment">-- Step 4: Final result - sum size grouped by root node</span></span><span>
</span><span><span class="hljs-keyword">SELECT</span></span><span> 
    fs.NodeID,
    fs.NodeName,
    </span><span><span class="hljs-built_in">SUM</span></span><span>(</span><span><span class="hljs-keyword">CASE</span></span><span> </span><span><span class="hljs-keyword">WHEN</span></span><span> ft.SizeBytes </span><span><span class="hljs-keyword">IS</span></span><span> </span><span><span class="hljs-keyword">NOT</span></span><span> </span><span><span class="hljs-keyword">NULL</span></span><span> </span><span><span class="hljs-keyword">THEN</span></span><span> ft.SizeBytes </span><span><span class="hljs-keyword">ELSE</span></span><span> </span><span><span class="hljs-number">0</span></span><span> </span><span><span class="hljs-keyword">END</span></span><span>) </span><span><span class="hljs-keyword">AS</span></span><span> sizeBytes
</span><span><span class="hljs-keyword">FROM</span></span><span> FileTree ft
</span><span><span class="hljs-keyword">JOIN</span></span><span> FileSystem fs </span><span><span class="hljs-keyword">ON</span></span><span> ft.RootID </span><span><span class="hljs-operator">=</span></span><span> fs.NodeID
</span><span><span class="hljs-keyword">GROUP</span></span><span> </span><span><span class="hljs-keyword">BY</span></span><span> fs.NodeID, fs.NodeName
</span><span><span class="hljs-keyword">ORDER</span></span><span> </span><span><span class="hljs-keyword">BY</span></span><span> fs.NodeID;
</span></span></code></div></div></pre>
