    </span><span><span class="hljs-comment">-- Start with each node as the root of its own tree</span></span><span>
    </span><span><span class="hljs-keyword">SELECT</span></span><span> 
        NodeID </span><span><span class="hljs-keyword">AS</span></span><span> RootID,
        NodeID </span><span><span class="hljs-keyword">AS</span></span><span> CurrentID,
        SizeBytes
    </span><span><span class="hljs-keyword">FROM</span></span><span> FileSystem

    </span><span><span class="hljs-keyword">UNION</span></span><span> </span><span><span class="hljs-keyword">ALL</span></span><span>

    </span><span><span class="hljs-comment">-- Recursively get children</span></span><span>
    </span><span><span class="hljs-keyword">SELECT</span></span><span>
        ft.RootID,
        fs.NodeID,
        fs.SizeBytes
    </span><span><span class="hljs-keyword">FROM</span></span><span> FileTree ft
    </span><span><span class="hljs-keyword">INNER</span></span><span> </span><span><span class="hljs-keyword">JOIN</span></span><span> FileSystem fs </span><span><span class="hljs-keyword">ON</span></span><span> fs.ParentID </span><span><span class="hljs-operator">=</span></span><span> ft.CurrentID
)

</span><span><span class="hljs-comment">-- Final output: sum SizeBytes for each Root folder</span></span><span>
</span><span><span class="hljs-keyword">SELECT</span></span><span> 
    fs.NodeID,
    fs.NodeName,
    </span><span><span class="hljs-built_in">SUM</span></span><span>(</span><span><span class="hljs-keyword">CASE</span></span><span> </span><span><span class="hljs-keyword">WHEN</span></span><span> ft.SizeBytes </span><span><span class="hljs-keyword">IS</span></span><span> </span><span><span class="hljs-keyword">NULL</span></span><span> </span><span><span class="hljs-keyword">THEN</span></span><span> </span><span><span class="hljs-number">0</span></span><span> </span><span><span class="hljs-keyword">ELSE</span></span><span> ft.SizeBytes </span><span><span class="hljs-keyword">END</span></span><span>) </span><span><span class="hljs-keyword">AS</span></span><span> sizeBytes
</span><span><span class="hljs-keyword">FROM</span></span><span> FileTree ft
</span><span><span class="hljs-keyword">JOIN</span></span><span> FileSystem fs </span><span><span class="hljs-keyword">ON</span></span><span> ft.RootID </span><span><span class="hljs-operator">=</span></span><span> fs.NodeID
</span><span><span class="hljs-keyword">GROUP</span></span><span> </span><span><span class="hljs-keyword">BY</span></span><span> fs.NodeID, fs.NodeName
</span><span><span class="hljs-keyword">ORDER</span></span><span> </span><span><span class="hljs-keyword">BY</span></span><span> fs.NodeID;
</span></span></code></div></div></pre>
